commits.content:
body {
  font-family: system-ui, -apple-system, Segoe UI, Roboto, sans-serif;
  margin: 0;
  padding: 0;
  background: #fafafa;
  color: #222;
}

main {
  max-width: 720px;
  margin: 24px auto;
  background: #fff;
  padding: 24px;
  border-radius: 16px;
  box-shadow: 0 4px 18px rgba(0, 0, 0, 0.06);
}

h1 {
  margin-top: 0;
}

section {
  margin: 16px 0 12px;
}

label {
  display: block;
  font-weight: 600;
  margin-bottom: 6px;
}

.row {
  display: flex;
  align-items: center;
  gap: 12px;
}

input[type="text"],
textarea {
  width: 100%;
  padding: 10px 12px;
  border: 1px solid #ddd;
  border-radius: 10px;
  font-size: 14px;
  background: #fff;
}

footer {
  display: flex;
  align-items: center;
  gap: 12px;
  margin-top: 18px;
}

button#save {
  padding: 8px 14px;
  border-radius: 10px;
  border: 1px solid #ddd;
  background: #fff;
  cursor: pointer;
  font-weight: 600;
}

button#save:hover {
  background: #f5f5f5;
}

#status {
  min-height: 1em;
  color: #0a7a0a;
  font-weight: 600;
}
